from typing import List, Tuple

class Solution:
    ans = 0
    def islandPerimeter(self, grid: List[List[int]]) -> int:
        self.ans = 0
        move = [(-1, 0), (1, 0), (0, 1), (0, -1)]

        def findIsland(grid: List[List[int]]) -> Tuple[int, int]:
            n, m = len(grid), len(grid[0])
            for i in range(n):
                for j in range(m):
                    if grid[i][j] == 1:
                        return (i, j)
            return (-1, -1)

        def debug(grid: List[List[int]], ans: int) -> None:
            for col in grid:
                print(col)
            print(f'ans: {self.ans}\n\n')
            return

        def getPerimeter(x, y: int, grid: List[List[int]]) -> None:
            if x < 0 or y < 0 or x >= len(grid) or y >= len(grid[0]):
                return

            if grid[x][y] == 0 or grid[x][y] == 2:
                return

            # debug(grid, self.ans)

            if x == 0:
                self.ans += 1
            if x > 0 and grid[x-1][y] == 0:
                self.ans += 1

            if y == 0:
                self.ans += 1
            if y > 0 and grid[x][y-1] == 0:
                self.ans += 1

            if x == len(grid) - 1:
                self.ans += 1
            if x < len(grid) - 1 and grid[x+1][y] == 0:
                self.ans += 1

            if y == len(grid[0]) - 1:
                self.ans += 1
            if y < len(grid[0]) - 1 and grid[x][y+1] == 0:
                self.ans += 1

            grid[x][y] = 2

            for dx, dy in move:
                getPerimeter(x+dx, y+dy, grid)

            return

        startx, starty = findIsland(grid)

        if startx == -1 and starty == -1:
            return -1

        # print(f'x: {startx}, y: {starty}')

        getPerimeter(startx, starty, grid)
        return self.ans

